mapper.update:
update category
set name           = #{name},
    update_time    = now(),
    articles_total = #{articlesTotal}
WHERE id = #{id}
  and is_deleted = 0

mapper.select:
SELECT id, name, create_time, update_time, articles_total FROM category
WHERE is_deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND create_time >= #{startDate}


    AND create_time <= #{endDate}

ORDER BY create_time DESC
